import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, Button, TextField, Typography, Paper } from '@mui/material';
import AuthService from '../services/AuthService';

// Paleta de colores personalizada
const COLORS = {
  verdeTitulo: '#059669',
  verdeAccion: '#10b981',
  verdeIA: '#34d399',
  fondo: '#f3f4f6',
  card: '#ffffff',
};

function LoginPage({ setIsAuthenticated }) {
  const [nombre, setNombre] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await AuthService.login(nombre, password);
      if (res.success) {
        setIsAuthenticated(true);
        navigate('/productos');
      } else {
        setError(res.message || 'Error de autenticaci칩n');
      }
    } catch (err) {
      setError('Error de servidor');
    }
  };

  return (
    <Box display="flex" justifyContent="center" alignItems="center" minHeight="100vh" bgcolor={COLORS.fondo} position="relative">
      {/* Marca POLIXAI en la parte superior izquierda */}
      <Box position="absolute" top={24} left={48} zIndex={2}>
        <Typography variant="h4" fontWeight="bold" fontFamily="monospace" letterSpacing={2} color={COLORS.verdeTitulo}>
          POLIXAI
        </Typography>
      </Box>
      <Paper elevation={6} sx={{ p: 4, minWidth: 320, background: COLORS.card, color: COLORS.verdeTitulo }}>
        <Typography variant="h5" mb={2} color={COLORS.verdeTitulo}>Iniciar Sesi칩n</Typography>
        <form onSubmit={handleSubmit}>
          <TextField label="Usuario" fullWidth margin="normal" value={nombre} onChange={e => setNombre(e.target.value)} required InputLabelProps={{ style: { color: COLORS.verdeTitulo } }} InputProps={{ style: { color: COLORS.verdeTitulo } }} />
          <TextField label="Contrase침a" type="password" fullWidth margin="normal" value={password} onChange={e => setPassword(e.target.value)} required InputLabelProps={{ style: { color: COLORS.verdeTitulo } }} InputProps={{ style: { color: COLORS.verdeTitulo } }} />
          {error && <Typography color="error" variant="body2">{error}</Typography>}
          <Button type="submit" variant="contained" fullWidth sx={{ mt: 2, bgcolor: COLORS.verdeAccion, '&:hover': { bgcolor: COLORS.verdeTitulo } }}>
            Entrar
          </Button>
        </form>
      </Paper>
    </Box>
  );
}

export default LoginPage;
