import axios from 'axios';

const API_URL = 'http://localhost:5000';

const CategoriasService = {
  getAll: async () => {
    try {
      const res = await axios.get(`${API_URL}/categorias`);
      return res.data;
    } catch (err) {
      return { data: [] };
    }
  }
};

export default CategoriasService;
