import React, { useEffect, useState } from 'react';
import { Box, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, CircularProgress } from '@mui/material';
import ProductosService from '../services/ProductosService';
import COLORS from '../theme/colors';

function ProductosPage() {
  const [productos, setProductos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    ProductosService.getAll().then(data => {
      setProductos(data.productos || []);
      setLoading(false);
    });
  }, []);

  return (
    <Box p={3} bgcolor={COLORS.grisClaro} minHeight="100vh">
      <Typography variant="h4" mb={2} color={COLORS.verdeOscuro} fontWeight="bold">Productos</Typography>
      {loading ? <CircularProgress sx={{ color: COLORS.verdeBrillante }} /> : (
        <TableContainer component={Paper} sx={{ background: COLORS.blanco, borderRadius: 3, boxShadow: `0 2px 8px 0 ${COLORS.verdeSuave}` }}>
          <Table>
            <TableHead sx={{ background: COLORS.verdeSuave }}>
              <TableRow>
                <TableCell sx={{ color: COLORS.verdeOscuro, fontWeight: 'bold' }}>ID</TableCell>
                <TableCell sx={{ color: COLORS.verdeOscuro, fontWeight: 'bold' }}>Nombre</TableCell>
                <TableCell sx={{ color: COLORS.verdeOscuro, fontWeight: 'bold' }}>Descripción</TableCell>
                <TableCell sx={{ color: COLORS.verdeOscuro, fontWeight: 'bold' }}>Precio Compra</TableCell>
                <TableCell sx={{ color: COLORS.verdeOscuro, fontWeight: 'bold' }}>Precio Venta</TableCell>
                <TableCell sx={{ color: COLORS.verdeOscuro, fontWeight: 'bold' }}>Stock Actual</TableCell>
                <TableCell sx={{ color: COLORS.verdeOscuro, fontWeight: 'bold' }}>Stock Mínimo</TableCell>
                <TableCell sx={{ color: COLORS.verdeOscuro, fontWeight: 'bold' }}>Categoría</TableCell>
                <TableCell sx={{ color: COLORS.verdeOscuro, fontWeight: 'bold' }}>Proveedor</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {productos.map((p, idx) => (
                <TableRow key={idx} hover sx={{ '&:hover': { background: COLORS.verdeSuave } }}>
                  <TableCell>{p[0]}</TableCell>
                  <TableCell>{p[1]}</TableCell>
                  <TableCell>{p[2]}</TableCell>
                  <TableCell>{p[3]}</TableCell>
                  <TableCell>{p[4]}</TableCell>
                  <TableCell>{p[5]}</TableCell>
                  <TableCell>{p[6]}</TableCell>
                  <TableCell>{p[7]}</TableCell>
                  <TableCell>{p[8]}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </Box>
  );
}

export default ProductosPage;
