import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, Button, TextField, Typography, Paper } from '@mui/material';
import AuthService from '../services/AuthService';

function LoginPage({ setIsAuthenticated }) {
  const [nombre, setNombre] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await AuthService.login(nombre, password);
      if (res.success) {
        setIsAuthenticated(true);
        navigate('/productos');
      } else {
        setError(res.message || 'Error de autenticaci칩n');
      }
    } catch (err) {
      setError('Error de servidor');
    }
  };

  return (
    <Box display="flex" justifyContent="center" alignItems="center" minHeight="100vh" bgcolor="#f5f5f5" position="relative">
      {/* Marca POLIXAI en la parte superior derecha */}
      <Box position="absolute" top={24} right={48} zIndex={2}>
        <Typography variant="h5" color="primary" fontWeight="bold" fontFamily="monospace" letterSpacing={2}>
          POLIXAI
        </Typography>
      </Box>
      <Paper elevation={3} sx={{ p: 4, minWidth: 320 }}>
        <Typography variant="h5" mb={2}>Iniciar Sesi칩n</Typography>
        <form onSubmit={handleSubmit}>
          <TextField label="Usuario" fullWidth margin="normal" value={nombre} onChange={e => setNombre(e.target.value)} required />
          <TextField label="Contrase침a" type="password" fullWidth margin="normal" value={password} onChange={e => setPassword(e.target.value)} required />
          {error && <Typography color="error" variant="body2">{error}</Typography>}
          <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>Entrar</Button>
        </form>
      </Paper>
    </Box>
  );
}

export default LoginPage;
