import React, { useState } from 'react';
import { Box, IconButton, List, ListItem, ListItemIcon, ListItemText, Tooltip } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import DashboardIcon from '@mui/icons-material/Dashboard';
import InventoryIcon from '@mui/icons-material/Inventory';
import PeopleIcon from '@mui/icons-material/People';
import './SidebarResponsive.css';

const items = [
  { icon: <DashboardIcon />, text: 'Dashboard', path: '/dashboard' },
  { icon: <InventoryIcon />, text: 'Inventario', path: '/productos' },
  { icon: <PeopleIcon />, text: 'Clientes', path: '/clientes' },
];

function SidebarResponsive() {
  const [open, setOpen] = useState(true);

  return (
    <Box
      className={`sidebar-responsive ${open ? 'sidebar-open' : 'sidebar-closed'}`}
      sx={{ height: '100vh', bgcolor: '#fff', borderRight: '1px solid #e5e7eb', transition: 'all 0.3s' }}
    >
      <Box display="flex" justifyContent={open ? 'flex-end' : 'center'} alignItems="center" p={1}>
        <IconButton onClick={() => setOpen(!open)}>
          {open ? <ChevronLeftIcon /> : <MenuIcon />}
        </IconButton>
      </Box>
      <List>
        {items.map(({ icon, text, path }) => (
          <ListItem button key={text} component="a" href={path}>
            <Tooltip title={open ? '' : text} placement="right">
              <ListItemIcon>{icon}</ListItemIcon>
            </Tooltip>
            {open && <ListItemText primary={text} />}
          </ListItem>
        ))}
      </List>
    </Box>
  );
}

export default SidebarResponsive;
