import React, { useState } from 'react';
import { Box, Typography, Paper, Button } from '@mui/material';
import FacturasFormModal from '../components/FacturasFormModal';
import ClientesService from '../services/ClientesService';
import ProductosService from '../services/ProductosService';
import FacturasService from '../services/FacturasService';
import { useNavigate } from 'react-router-dom';

function FacturaFullPage() {
  const [clientes, setClientes] = useState([]);
  const [productos, setProductos] = useState([]);
  const navigate = useNavigate();
  React.useEffect(() => {
    ClientesService.getAll().then(data => setClientes(data.data || []));
    ProductosService.getAll().then(data => setProductos(data.productos || []));
  }, []);

  const handleSubmit = async (form) => {
    try {
      await FacturasService.create(form);
      navigate('/facturas');
    } catch (err) {
      alert('Error al guardar la factura');
    }
  };

  return (
    <Box p={4} display="flex" justifyContent="center" alignItems="flex-start" minHeight="100vh" bgcolor="#f7fafc">
      <Paper sx={{ p: 4, minWidth: 400, maxWidth: 600, width: '100%' }}>
        <Typography variant="h6" mb={2} fontWeight="bold">Nueva Factura</Typography>
        <FacturasFormModal open={true} onClose={() => navigate('/facturas')} onSubmit={handleSubmit} clientes={clientes} productos={productos} />
        <Button sx={{ mt: 2 }} variant="outlined" color="secondary" onClick={() => navigate('/facturas')}>Cancelar</Button>
      </Paper>
    </Box>
  );
}

export default FacturaFullPage;
